import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { getAuth, signOut } from 'firebase/auth';
import '../Styles/Nav.css';


const Nav = () =>{
    const auth = getAuth();

    const handleLogout = () => {
    signOut(auth)
      .then(() => {
        console.log("Sesión cerrada exitosamente");
      })
      .catch((error) => {
        console.error("Error al cerrar sesión: ", error);
      });
    };
    return(
        <div className="side-bar">
            <Sidebar>
            <Menu menuItemStyles = { { 
      button : { 
        // la clase activa será añadida automáticamente por react router 
        // para que podamos usarla para darle estilo al elemento de menú activo 
        [ `&.active` ] : { 
          backgroundColor : '#13395e' , 
          color : '#b6c8d9' , 
        } , 
      } , 
    } } >
            <MenuItem>VivaMail</MenuItem>
            <SubMenu label="Configuración administradores">
            <MenuItem> Añadir Administrador </MenuItem>
            <MenuItem> Borrar Administrador </MenuItem>
            <MenuItem> Modificar Administrador </MenuItem>
            </SubMenu>
            <MenuItem> Reportes </MenuItem>
            <MenuItem> Planes </MenuItem>
            <MenuItem><button onClick={handleLogout}>Cerrar sesión</button></MenuItem>
            </Menu>
            </Sidebar>
        </div>
    );
};

export default Nav;
